sys_script_include.script:
function DevToolsGetLinkDirectory(linkdirectory)
{
	linkdirectory['GeoData'] =
	[
		{
			title: 'European Union',
			url: 'https://en.wikipedia.org/wiki/European_Union'
		},
		{
			title: 'NATO',
			url: 'https://www.nato.int/cps/en/natohq/nato_countries.htm'
		}
	];
	return linkdirectory;
}
